mapper.delete:
delete from preferential
where id in

	#{item}

mapper.select:
select p.*,s.shop_name as shopName  from preferential p inner  join shop s on p.shop_id = s.id
where 1=1

    and s.shop_name like "%"#{shop}"%"


    and p.type = #{type}


    and p.status = #{status}


    and p.title like "%"#{title}"%"


    and  #{startTime} <= p.end_time


    and  #{endTime} >= p.start_time

order by p.update_time desc

    limit #{start},#{size}
