mapper.select:
select distinct u.email from operate o inner join role_oper r on o.id = r.oper_id inner join user_role ru on ru.role_id = r.role_id inner join user u on u.id = ru.user_id
where o.code in
#{item}

mapper.update:
update `user` set id = id

    , account = #{user.account}


    , username = #{user.username}


    , update_time = #{user.update_time}


    , password = #{user.password}


    , status = #{user.status}


    , update_user = #{user.update_user}


    , email = #{user.email}


    , auth_code = #{user.auth_code}


    , auth_time = #{user.auth_time}


    , auth_time = null


    , auth_code = null

where 1=1

    and id = #{user.id}
